import { createClient } from '@supabase/supabase-js';
import type { Database } from './types';

// Environment variables - NO fallbacks in production
const SUPABASE_URL = import.meta.env.VITE_SUPABASE_URL;
const SUPABASE_PUBLISHABLE_KEY = import.meta.env.VITE_SUPABASE_ANON_KEY;

// Validate required environment variables
if (!SUPABASE_URL || !SUPABASE_PUBLISHABLE_KEY) {
  const missingVars = [];
  if (!SUPABASE_URL) missingVars.push('VITE_SUPABASE_URL');
  if (!SUPABASE_PUBLISHABLE_KEY) missingVars.push('VITE_SUPABASE_ANON_KEY');

  const errorMessage = `Missing required environment variables: ${missingVars.join(
    ', '
  )}. Please configure these in your deployment environment.`;

  // In development, show helpful error
  if (import.meta.env.DEV) {
    console.error('‚ùå Supabase Configuration Error:', errorMessage);
    console.log('üí° Create a .env file with your Supabase credentials.');
    console.log('üìñ See .env.example for reference.');
  }

  throw new Error(errorMessage);
}

// Import the supabase client like this:
// import { supabase } from "@/integrations/supabase/client";

export const supabase = createClient<Database>(SUPABASE_URL, SUPABASE_PUBLISHABLE_KEY, {
  auth: {
    storage: typeof window !== 'undefined' ? localStorage : undefined,
    persistSession: true,
    autoRefreshToken: true,
  },
});
